(49, 'Dr. Strangelove or: How I Learned to Stop Worrying', '1964-01-29', 95, 'An insane general triggers a path to nuclear holocaust that a War Room full of politicians and generals frantically tries to stop.', 'Comedy', '8.4', 'https://m.media-amazon.com/images/M/MV5BZWI3ZTMxNjctMjdlNS00NmUwLWFiM2YtZDUyY2I3N2MxYTE0XkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_SX300.jpg', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/769ol0IgWKE?si=muwr11XT96fuik_w\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" referrerpolicy=\"strict-origin-when-cross-origin\" allowfullscreen></iframe>', 'https://www.viroquachamber.com/wp-content/uploads/2023/11/DrStrangelove.png', 'Edward Zwick', 'Jake Gyllenhaal, Anne Hathaway, Judy Greer'),
(50, 'Love Actually', '2003-11-14', 135, 'Follows the lives of eight very different couples in dealing with their love lives in various loosely interrelated tales all set during a frantic month before Christmas in London, England.', 'Comedy', '7.6', 'https://m.media-amazon.com/images/M/MV5BMTY4NjQ5NDc0Nl5BMl5BanBnXkFtZTYwNjk5NDM3._V1_SX300.jpg', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/H9Z3_ifFheQ?si=-QxFMxfOnKadgIvX\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" referrerpolicy=\"strict-origin-when-cross-origin\" allowfullscreen></iframe>', 'https://m.media-amazon.com/images/S/pv-target-images/a24f6ae105b21e41b7b38f3a8ac2cf0e9636b68c9abb3d37a2ead19d3d43a6fa._SX1080_FMjpg_.jpg', 'Paul Thomas Anderson', 'Adam Sandler, Emily Watson'),
(51, 'Shakespeare in Love', '1998-01-08', 123, 'A young Shakespeare, out of ideas and short of cash, meets his ideal woman and is inspired to write one of his most famous plays.', 'Comedy', '7.1', 'https://m.media-amazon.com/images/M/MV5BM2ZkNjM5MjEtNTBlMC00OTI5LTgyYmEtZDljMzNmNzhiNzY0XkEyXkFqcGdeQXVyNDYyMDk5MTU@._V1_SX300.jpg', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/gk1rTKB6ZF8?si=bbkbaQ1CKtot2q50\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share\" referrerpolicy=\"strict-origin-when-cross-origin\" allowfullscreen></iframe>', 'https://cdn.britannica.com/54/71654-050-5389285A/Judi-Dench-Colin-Firth-Shakespeare-in-Love.jpg', 'Pierre Morel', 'John Travolta, Kasia Smutniak'),
